import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import jwt_decode from "jwt-decode";


const loginUser = async (e) => {
  const dispatch = useDispatch();

  const navigate = useNavigate();
  e.preventDefault();


  try {

    const response = await axios.post("http://127.0.0.1:8000/auth/jwt/create/", {
      "email": e.target.email.value,
      "password": e.target.password.value

    }

    );


    const { access, refresh } = response.data;
    console.log("ðŸš€ ~ file: LoginPage.jsx:30 ~ loginUser ~ refresh:", refresh);
    console.log("ðŸš€ ~ file: LoginPage.jsx:30 ~ loginUser ~ response:", response);
    if (response.status === 200) {
      dispatch(updateAuth(true));
      dispatch(setUser(jwt_decode(access)));
      dispatch(setTokens({ access, refresh }));

      // setAuthTokens(data);
      // setUser(jwt_decode(data.access));
      localStorage.setItem('Tokens', JSON.stringify(response.data));
      console.log("ðŸš€ ~ file: LoginPage.jsx:39 ~ loginUser ~ response.data:", response.data);
      navigate('/');
    } else {
      alert('Something went wrong!');
    }

  } catch (error) {
    alert('Something went wrong!' + error.response.status);

  }


};


const initialState = {
  tokens: {},
  login: loginUser,
  user: {},
  full: false,
  auth: false,
};

export const getTokens = createAsyncThunk("getTokens", async () => {
  const { data } = await axios.post("http://127.0.0.1:8000/auth/jwt/create/", {
    "email": "moustafashahin122@gmail.com",
    "password": "lovelove122@A"

  }
  );
  console.log(data);
  // console.log("ðŸš€ ~ file: TokensSlice.jsx:23 ~ getTokens ~ data:", data);
  return data;
});
const TokensSlice = createSlice({
  name: "Tokens",
  initialState,
  reducers: {


    setTokens: (state, action) => {

      const { access, refresh } = action.payload;
      // console.log("ðŸš€ ~ file: TokensSlice.jsx:34 ~ access:", access);
      state.tokens.access = access;
      state.tokens.refresh = refresh;
    },
    setUser: (state, action) => {

      const { user_id } = action.payload;
      state.user.user_id = user_id;
      console.log("ðŸš€ ~ file: TokensSlice.jsx:44 ~ user_id:", user_id);

    },
    updateAuth: (state, action) => {
      state.auth = action.payload;


    },
    refreshTokens: (state, action) => {
      state.auth = action.payload;


    },


  },

  extraReducers: (builder) => {
    builder.addCase(getTokens.fulfilled, (state, action) => {
      state.tokens = action.payload;
      // state.tokens.access = access;
      // state.tokens.refresh = refresh;
      // console.log("dssssssssssssssssssssssssssssss", state.tokens.access);
      state.full = true;
    });
  },
});
export const { setTokens, updateAuth, setUser } = TokensSlice.actions;

export default TokensSlice.reducer;
