import HomePage from "./pages/HomePage";
import LoginPage from "./pages/LoginPage";
// import PrivateComponent from './utils/PrivateComponent';
import React, { useState, useEffect, useRef } from "react";
import { Link, Navigate, Route, Routes, useNavigate } from 'react-router-dom';
import axios from "axios";
import { BrowserRouter } from "react-router-dom";
import TestAxios from "./pages/TestAxios";
import { useDispatch, useSelector } from "react-redux";
import { getTokens, refreshTokens, setTokens, setUser, updateAuth, logout, setupdateTokensfun, setRunningInterval } from "./redux/slices/TokensSlice";
import cv from './mah.png';
import jwt_decode from "jwt-decode";
import { applyMiddleware } from "@reduxjs/toolkit";

const App = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const fourMinutes = 2000;

  const tokens = useSelector((state) => state.TokensSlice.tokens);
  const tokensRef = useRef({});
  const auth = useSelector((state) => state.TokensSlice.auth);
  const user = useSelector((state) => state.TokensSlice.user);
  const loggedin = useSelector((state) => state.TokensSlice.loggedin);
  const full = useSelector((state) => state.TokensSlice.full);
  tokensRef.current = tokens;




  const [tokens_from_local_Storage, setlocals] = useState(() => (localStorage.getItem('Tokens') ? JSON.parse(localStorage.getItem('Tokens')) : null));




  if (tokens_from_local_Storage && tokens_from_local_Storage.refresh) {
    console.log("++++++++++++++++local storage+++++++++++++++++++++++++++++++++++++++");

    dispatch(setTokens({ refresh: tokens_from_local_Storage.refresh, access: tokens_from_local_Storage.access }));

  } else {
    alert("local storage is empty");
  }






  if (full === true) {
    // console.log("ðŸš€ ~ file: App.jsx:14 ~ App ~ tokens:", tokens);

  }


  // dispatch(setTokens({ access: "sdfdsf", refresh: "sdfsf" }));


const intervalRef =useRef({})

  useEffect(() => {
    if (!loggedin) {

        if (tokens_from_local_Storage && tokens_from_local_Storage.refresh) {

          dispatch(refreshTokens(tokens_from_local_Storage.refresh));


          intervalRef.current = setInterval(() => {
            dispatch(refreshTokens(tokens.refresh));
          }
            , fourMinutes);



          dispatch(setRunningInterval(intervalRef.current));

        }
        else {
          handleLogout();

        }


    }



  }, []);


  // useEffect(() => {


  // }, [tokens]);





  const [pe, setpe] = useState("");
  // useEffect(() => {
  //   const fetchData = async () => {
  //     const res = await axios.post("http://127.0.0.1:8000/auth/jwt/create/", {
  //       "email": "moustafashahin122@gmail.com",
  //       "password": "lovelove122@A"

  //     }
  //     );
  //     console.log("ðŸš€ ~ file: TestAxios.jsx:13 ~ fetchData ~ res:", res);

  //     setpe(await JSON.stringify(res.data.access));
  //   };
  //   fetchData();

  // // }, []);
  const handleLogout = () => {





    dispatch(logout());
    navigate("/login");

  };



  return (
    <>
      <button onClick={handleLogout}>logout</button>
      <div className="App">
        <Link to='/'>home</Link> <br />
        <Link to='/login'>login</Link>
        <Routes>
          <Route element={auth ? < HomePage /> : <Navigate to="/login" />} path="/" exact />
          <Route element={<LoginPage />} path="/login" />
          <Route element={<TestAxios />} path="/test" />
        </Routes>
      </div>
      {/* {pe} */}
      {auth ? (
        // tokens.access
        auth

      ) : (
        <p>not found



          < img src={cv} height="500px" alt="" />
        </p>
      )}
      <p>home</p>
      <p>{auth}</p>
      {/* <p>{tokens.access}</p> */}
      {/* <p>{tokens.refresh}</p> */}
    </>
  );
};

export default App;
